import java.io.IOException
import java.text.SimpleDateFormat
import java.util.zip.Deflater
import java.util.zip.DeflaterOutputStream
import java.util.zip.Inflater
import java.util.zip.InflaterOutputStream
import java.util.zip.ZipException

import javax.xml.bind.DatatypeConverter

import org.w3c.dom.Document
import org.w3c.dom.Element
import org.w3c.dom.Node
import org.w3c.dom.NodeList

import com.vordel.circuit.CircuitAbortException
import com.vordel.circuit.Message
import com.vordel.mime.XMLBody

boolean invoke(Message msg) {
	if (!msg.containsKey("saml.library")) {
		msg.put("saml.library", new SamlSelectors(msg));
	}
	return true;
}

public class SamlSelectors {
	/**
	 * Date format for Saml Issue Instant
	 */
	private final SimpleDateFormat dateFormat;
	private final Message msg;

	public SamlSelectors(Message msg) {
		SimpleDateFormat dateFormat = new SimpleDateFormat("yyyy-MM-dd'T'HH:mm:ss'Z'");

		/* set the XML date format timezone */
		dateFormat.setTimeZone(TimeZone.getTimeZone("UTC"));

		this.dateFormat = dateFormat;
		this.msg = msg;
	}

	private void storeException(CircuitAbortException cause) {
		if (cause != null) {
			msg.put("circuit.exception", cause);
			msg.put("circuit.failure.reason", cause.getMessage());
		}
	}

	public boolean hasPendingException() {
		Object exception = msg.get("circuit.exception");

		return exception instanceof CircuitAbortException;
	}

	private String encodeSamlMessage(String decoded, boolean deflate) throws CircuitAbortException {
		ByteArrayOutputStream deflated = new ByteArrayOutputStream();

		try {
			OutputStream output = deflated;

			if (deflate) {
				Deflater deflater = new Deflater(Deflater.DEFAULT_COMPRESSION, true);

				output = new DeflaterOutputStream(deflated, deflater);
			}

			try {
				output.write(decoded.getBytes("UTF-8"));
			} finally {
				output.close();
			}
		} catch (UnsupportedEncodingException e) {
			throw new CircuitAbortException("UTF-8 is not supported", e);
		} catch (IOException e) {
			throw new CircuitAbortException("Unable to encode message", e);
		}

		/* convert the output to a byte array and encode it using base 64 */
		return DatatypeConverter.printBase64Binary(deflated.toByteArray());
	}

	private String decodeSamlMessage(String encoded, boolean inflate) throws CircuitAbortException {
		ByteArrayOutputStream inflated = new ByteArrayOutputStream();
		String decoded = null;

		try {
			byte[] input = DatatypeConverter.parseBase64Binary(encoded);
			OutputStream output = inflated;

			if (inflate) {
				Inflater inflater = new Inflater(true);

				output = new InflaterOutputStream(inflated, inflater);
			}

			try {
				output.write(input);
			} catch (ZipException e) {
				inflated.reset();
				inflated.write(input);
			} finally {
				output.close();
			}

			/* convert the output to string */
			decoded = new String(inflated.toByteArray(), "UTF-8");
		} catch (UnsupportedEncodingException e) {
			throw new CircuitAbortException("UTF-8 is not supported", e);
		} catch (IOException e) {
			throw new CircuitAbortException("Unable to decode message", e);
		}

		return decoded;
	}

	public String generateIssueInstant() {
		Date instant = new Date();

		return dateFormat.format(instant);
	}

	public String generateSamlId() {
		UUID uuid = UUID.randomUUID();

		/*
		 * prefix the uuid by '_' to avoid an invalid document being
		 * generated (XML IDs can't start with digits)
		 */
		return "_" + uuid.toString();
	}

	public String generateRelayStateTag(String relayState) {
		StringBuilder relayStateTag = new StringBuilder();

		if ((!isNullValue(relayState)) && (relayState.length() > 0)) {
			relayStateTag.append("<input type=\"hidden\" name=\"RelayState\" value=\"");

			for (char c : relayState.toCharArray()) {
				switch (c) {
					case '&':
						relayStateTag.append("&amp;");
						break;
					case '"':
						relayStateTag.append("&quot;");
						break;
					default:
						relayStateTag.append(c);
				}
			}

			/* ends the tag */
			relayStateTag.append("\" />");
		}

		return relayStateTag.toString();
	}

	public String encodeAuthNRequest(String decoded) {
		String result = null;

		try {
			if (isNullValue(decoded)) {
				throw new CircuitAbortException("No AuthNRequest Provided");
			}

			result = encodeSamlMessage(decoded, true);
		} catch(CircuitAbortException e) {
			storeException(e);
		}

		return result;
	}

	public String decodeAuthNRequest(String encoded) {
		String result = null;

		try {
			if (isNullValue(encoded)) {
				throw new CircuitAbortException("No AuthNRequest Provided");
			}

			result = decodeSamlMessage(encoded, true);
		} catch(CircuitAbortException e) {
			storeException(e);
		}

		return result;
	}

	public String encodeSamlResponse(String decoded) {
		String result = null;

		try {
			if (isNullValue(decoded)) {
				throw new CircuitAbortException("No Response Provided");
			}

			result = encodeSamlMessage(decoded, false);
		} catch(CircuitAbortException e) {
			storeException(e);
		}

		return result;
	}

	public String decodeSamlResponse(String encoded) {
		String result = null;

		try {
			if (isNullValue(encoded)) {
				throw new CircuitAbortException("No Response Provided");
			}

			result = decodeSamlMessage(encoded, false);
		} catch(CircuitAbortException e) {
			storeException(e);
		}

		return result;
	}

	/**
	 * Unwrap existing assertions from EncryptedAssertion nodes after
	 * decryption
	 * 
	 * @return 'false' if any error occured
	 */
	public boolean unwrapAssertions() {
		boolean success = true;

		try {
			Document body = XMLBody.getDocument(msg);
			NodeList nodes = body.getElementsByTagNameNS("urn:oasis:names:tc:SAML:2.0:assertion", "EncryptedAssertion");

			for (int index = 0; index < nodes.getLength(); index++) {
				Element encrypted = (Element) nodes.item(index);
				Node parent = encrypted.getParentNode();
				NodeList childs = encrypted.getChildNodes();

				for (int child = 0; child < childs.getLength(); child++) {
					Node assertion = childs.item(child);

					if ((assertion.getNodeType() == Node.ELEMENT_NODE) && ("urn:oasis:names:tc:SAML:2.0:assertion".equals(assertion.getNamespaceURI())) && ("Assertion".equals(assertion.getLocalName()))) {
						encrypted.removeChild(assertion);
						parent.insertBefore(assertion, encrypted);
					}
				}

				parent.removeChild(encrypted);
			}
		} catch (IOException e) {
			storeException(new CircuitAbortException("Unable to retrieve XML body", e));

			success = false;
		}

		return success;
	}

	/**
	 * Check if an object is null
	 *
	 * @param value
	 *            value to be tested
	 * @return 'true' if the value is null
	 */
	public boolean isNullValue(Object value) {
		/*
		 * XXX Did not found another 'portable' way to handle missing
		 * properties. To be upgraded in the future
		 */
		if ((value instanceof String) && "[invalid field]".equals(value)) {
			value = null;
		}

		return value == null;
	}

	public boolean isEmptyString(String value) {
		if ((value != null) && (value.trim().isEmpty())) {
			value = null;
		}

		return isNullValue(value);
	}
}
